from PIL import Image
from io import BytesIO
from base64 import b64decode


def image64toImage(image64):
	return Image.open(BytesIO(b64decode(image64)))


# Material icons (brightness_2 - looks like moon)
# base64 versions used because of pyinstaller not packing them into one executable without using temporary dirs
_disabledDark64 = ''
_disabledLight64 = ''
_enabledDark64 = ''
_enabledLight64 = ''

disabledDark = image64toImage(_disabledDark64)
disabledLight = image64toImage(_disabledLight64)
enabledDark = image64toImage(_enabledDark64)
enabledLight = image64toImage(_enabledLight64)
